import { Link } from "react-router-dom";
import cortina1 from "../assets/cortinaRollerBlackOut.jpg";
import cortina2 from "../assets/cortinaRollerSistemaDoble.jpg";
import persiana from "../assets/cortinaPersianaAmericana.jpg";
import sistema from "../assets/sistema.jpg";

const imagenes = {
  "1": cortina1,  "2": cortina2,  "3": persiana,  "4": sistema};

function Item({ item }) {
  return (
    <div className="card" style={{ width: '18rem', margin: '10px' }}>
      <img
        src={item.imagen}
        className="card-img-top"
        alt={item.nombre}
        style={{ height: '200px', objectFit: 'cover' }}
      />
      <div className="card-body">
        <h5 className="card-title">{item.nombre}</h5>
        <p className="card-text">${item.precio}</p>
        <Link to={`/item/${item.id}`} className="btn btn-primary">Ver detalle</Link>
      </div>
    </div>
  );
}

export default Item;
