import { useCart } from "../context/CartContext";

function CartWidget() {
  const { cart } = useCart();
  const totalUnidades = cart.reduce((acc, item) => acc + item.cantidad, 0);

  return (
    <div style={{ position: 'relative', display: 'inline-block' }}>
      {/* √çcono del carrito */}
      <span className="fs-4">üõí</span>

      {/* Burbuja solo si hay productos */}
      {totalUnidades > 0 && (
        <span 
          style={{
            position: 'absolute',
            top: '-5px',
            right: '-10px',
            background: 'red',
            color: 'white',
            borderRadius: '50%',
            padding: '2px 6px',
            fontSize: '0.75rem',
            fontWeight: 'bold'
          }}
        >
          {totalUnidades}
        </span>
      )}
    </div>
  );
}

export default CartWidget;
